package Hello;

public class BasicOperations 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a = 7, b = 11;
		
		System.out.println("a is: "+a);
		System.out.println("b is: " + b);
		System.out.println(a == b);
		System.out.println(a != b);
		System.out.println(a > b);
		System.out.println(a < b);
		System.out.println(a >= b);
		System.out.println(a <= b);


	}

}
