package Hello;

public class IfElse
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int number = 10;
		if (number == 0)
		     System.out.println("The number is Neutral(ZERO)");
		else if (number < 0)
			System.out.println("The number is Negative");
		else
		     System.out.println("The number is Positive");
		


	}

}
